mapper.select:
SELECT COUNT(*)
FROM yboard


		AND  ${searchColumn}  LIKE CONCAT('',#{searchText},'%')



			AND priority >= #{priority}



ORDER BY registDate DESC LIMIT #{start}, #{limit}

SELECT boardID, boardTitle, priority, userName, userGender, registDate
FROM yboard


		AND  ${searchColumn}  LIKE CONCAT('',#{searchText},'%')



			AND priority >= #{priority}



ORDER BY registDate DESC LIMIT #{start}, #{limit}
